mapper.insert:
INSERT INTO owners (
    first_name, last_name, address, city, telephone
) VALUES (
    #{firstName},
    #{lastName},
    #{address},
    #{city},
    #{telephone}
)

mapper.update:
UPDATE owners
SET
    first_name = #{firstName}
  , last_name = #{lastName}
  , address = #{address}
  , city = #{city}
  , telephone = #{telephone}
WHERE
    id = #{id}
